            {/* Your existing code... */}
          </div>
        )}
        {showFood && (
          <div className="module-card">
            {/* <img src={food[0].image} alt="Pongal" /> */}
            <h3>Food</h3>
            {/* Your existing code... */}
          </div>
        )}
        {showArtifacts && (
          <div className="module-card">
            {/* <img src={artifacts[0].image} alt="Buddhist Artifacts" /> */}
            <h3>Artifacts</h3>
            {/* Your existing code... */}
          </div>
        )}
        {showFestivals && (
          <div className="module-card">
            <img src={festivals[0].image} alt="Holi" />
            <h3>Festivals</h3>
            {/* Your existing code... */}
          </div>
        )}
        {showMusic && (
          <div className="module-card">
            <img src={music[0].image} alt="Indian Pop Music" />
            <h3>Music</h3>
            {/* Your existing code... */}
          </div>
        )}
      </div>
    </div>
  );
};

export default Module;
