import React, { useState } from 'react';

const Gallery = () => {
  const [showFestivals, setShowFestivals] = useState(false);
  const [showArchitecture, setShowArchitecture] = useState(false);
  const [showTraditionalAttire, setShowTraditionalAttire] = useState(false);
  const [showMusicalHeritage, setShowMusicalHeritage] = useState(false);
  const [showDanceForms, setShowDanceForms] = useState(false);

  const [newItemName, setNewItemName] = useState('');
  const [newItemDescription, setNewItemDescription] = useState('');

  const [festivals, setFestivals] = useState([
    { name: 'Diwali', description: 'Festival of lights celebrated across India' },
    // Add more festivals as needed
  ]);

  const [architecturalMarvels, setArchitecturalMarvels] = useState([
    { name: 'Taj Mahal', description: 'Iconic symbol of love in Agra, Uttar Pradesh' },
    // Add more architectural marvels as needed
  ]);

  // Add similar state variables and functions for other categories

  const handleFestivalClick = () => {
    setShowFestivals(true);
    setShowArchitecture(false);
    setShowTraditionalAttire(false);
    setShowMusicalHeritage(false);
    setShowDanceForms(false);
  };

  const handleArchitectureClick = () => {
    setShowFestivals(false);
    setShowArchitecture(true);
    setShowTraditionalAttire(false);
    setShowMusicalHeritage(false);
    setShowDanceForms(false);
  };

  // Add similar handle click functions for other categories

  const handleAddItem = () => {
    // Add new item based on the selected category
    const newItem = { name: newItemName, description: newItemDescription };
    if (showFestivals) setFestivals([...festivals, newItem]);
    else if (showArchitecture) setArchitecturalMarvels([...architecturalMarvels, newItem]);
    // Add similar conditions for other categories

    // Clear the input fields
    setNewItemName('');
    setNewItemDescription('');
  };

  const handleDeleteItem = (index) => {
    // Delete item based on the selected category and index
    if (showFestivals) {
      const updatedFestivals = [...festivals];
      updatedFestivals.splice(index, 1);
      setFestivals(updatedFestivals);
    }
    // Add similar conditions for other categories
  };

  return (
    <div className="gallery-container">
      <center>
        <h2>Gallery</h2>
        <div className="gallery-options">
          <table>
            <tbody>
              {/* ... existing code */}
            </tbody>
          </table>
        </div>
      </center>
      <div className="gallery-details">
        {showFestivals && (
          <div className="gallery-card">
            <h3>Festivals</h3>
            <input
              type="text"
              placeholder="Name"
              value={newItemName}
              onChange={(e) => setNewItemName(e.target.value)}
            />
            <input
              type="text"
              placeholder="Description"
              value={newItemDescription}
              onChange={(e) => setNewItemDescription(e.target.value)}
            />
            <button onClick={handleAddItem}>Add Festival</button>
            {festivals.map((festival, index) => (
              <div key={index}>
                <span>{festival.name}</span>
                <span>{festival.description}</span>
                <button onClick={() => handleDeleteItem(index)}>Delete</button>
              </div>
            ))}
          </div>
        )}
        {/* Add similar sections for other categories */}
      </div>
    </div>
  );
};

export default Gallery;
