import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import tirupati from '../images/tirupati.jpg';
import charminar from '../images/charminar.jpg';
import shiva from '../images/shiva.jpg';
import jatayu from '../images/jatayu.jpg';
import dudhsagarfalls from '../images/dudhsagarfalls.jpg';
import mysorepalace from '../images/mysorepalace.jpg';
import purijagannathtemple from '../images/purijagannathtemple.jpg';
import howrah from '../images/howrah.jpg';
import gateofindia from '../images/gateofindia.jpg';
import pinkcity from '../images/pinkcity.jpg';
import kedarnathtemple from '../images/kedarnathtemple.jpg';
import varanasi from '../images/varanasi.jpg';
import redfort from '../images/redfort.jpg';
import baltalvalley from '../images/baltalvalley.jpg';

const HeritageSites = () => {
    const location = useLocation();
    const username = location.state?.username || 'Guest';

    // State for toggling information display
    const [showTirupatiInfo, setShowTirupatiInfo] = useState(false);
    const [showCharminarInfo, setShowCharminarInfo] = useState(false);
    const [showShivaInfo, setShowShivaInfo] = useState(false);
    const [showJatayuInfo, setShowJatayuInfo] = useState(false);
    const [showDudhsagarInfo, setShowDudhsagarInfo] = useState(false);
    const [showMysorePalaceInfo, setShowMysorePalaceInfo] = useState(false);
    const [showPuriJagannathInfo, setShowPuriJagannathInfo] = useState(false);
    const [showHowrahInfo, setShowHowrahInfo] = useState(false);
    const [showGateOfIndiaInfo, setShowGateOfIndiaInfo] = useState(false);
    const [showPinkCityInfo, setShowPinkCityInfo] = useState(false);
    const [showKedarnathInfo, setShowKedarnathInfo] = useState(false);
    const [showVaranasiInfo, setShowVaranasiInfo] = useState(false);
    const [showRedFortInfo, setShowRedFortInfo] = useState(false);
    const [showBaltalValleyInfo, setShowBaltalValleyInfo] = useState(false);

    // Function to toggle information display for Tirupati
    const toggleTirupatiInfo = () => {
        setShowTirupatiInfo(!showTirupatiInfo);
    };

    // Function to toggle information display for Charminar
    const toggleCharminarInfo = () => {
        setShowCharminarInfo(!showCharminarInfo);
    };

    // Function to toggle information display for Shiva
    const toggleShivaInfo = () => {
        setShowShivaInfo(!showShivaInfo);
    };

    // Function to toggle information display for Jatayu
    const toggleJatayuInfo = () => {
        setShowJatayuInfo(!showJatayuInfo);
    };

    // Function to toggle information display for Dudhsagar Falls
    const toggleDudhsagarInfo = () => {
        setShowDudhsagarInfo(!showDudhsagarInfo);
    };

    // Function to toggle information display for Mysore Palace
    const toggleMysorePalaceInfo = () => {
        setShowMysorePalaceInfo(!showMysorePalaceInfo);
    };

    // Function to toggle information display for Puri Jagannath Temple
    const togglePuriJagannathInfo = () => {
        setShowPuriJagannathInfo(!showPuriJagannathInfo);
    };

    // Function to toggle information display for Howrah
    const toggleHowrahInfo = () => {
        setShowHowrahInfo(!showHowrahInfo);
    };

    // Function to toggle information display for Gate of India
    const toggleGateOfIndiaInfo = () => {
        setShowGateOfIndiaInfo(!showGateOfIndiaInfo);
    };

    // Function to toggle information display for Pink City
    const togglePinkCityInfo = () => {
        setShowPinkCityInfo(!showPinkCityInfo);
    };

    // Function to toggle information display for Kedarnath Temple
    const toggleKedarnathInfo = () => {
        setShowKedarnathInfo(!showKedarnathInfo);
    };

    // Function to toggle information display for Varanasi
    const toggleVaranasiInfo = () => {
        setShowVaranasiInfo(!showVaranasiInfo);
    };

    // Function to toggle information display for Red Fort
    const toggleRedFortInfo = () => {
        setShowRedFortInfo(!showRedFortInfo);
    };

    // Function to toggle information display for Baltal Valley
    const toggleBaltalValleyInfo = () => {
        setShowBaltalValleyInfo(!showBaltalValleyInfo);
    };

    return (
        <div>
            <center>
                <h1>Few heritage sites of India!!</h1>
                
                <div style={{ display: 'flex', flexDirection: 'row', justifyContent: 'center', flexWrap: 'wrap' }}>
                    {/* Tirupati Image */}
                    <div style={{ marginRight: '20px' }}>
                        <img src={tirupati} alt="Tirupati" width="300" height="200" onClick={toggleTirupatiInfo} style={{ cursor: 'pointer' }} />
                        <h3 >ANDHARA PRADESH</h3>
                    </div>
                    {showTirupatiInfo && (
                        <table style={{ border: '3px solid black', backgroundColor: '#FFE4E1' }}>
                            <tbody>
                                <tr>
                                    <th>Information</th>
                                </tr>
                                <tr>
                                    <td>It is the 7th most populous urban agglomeration in the state, with a population of 459,985 in 2011. [3] As of 2011 census, it had a population of 287,035 making it the 9th most populous city in Andhra Pradesh.[3] It is the second biggest city in Rayalaseema after Kurnool. For the year 2012–2013, India's Ministry of Tourism named Tirupati as the "Best Heritage City".[10] Tirupati has been selected as one of the hundred Indian cities to be developed as a smart city under Smart Cities Mission by Government of India..</td>
                                </tr>
                            </tbody>
                        </table>
                    )}

                    {/* Charminar Image */}
                    <div style={{ marginRight: '20px' }}>
                        <img src={charminar} alt="Charminar" width="300" height="200" onClick={toggleCharminarInfo} style={{ cursor: 'pointer' }} />
                        <h3>TELAGANA</h3>
                    </div>
                    {showCharminarInfo && (
                        <table style={{ border: '3px solid black', backgroundColor: '#ADD8E6' }}>
                            <tbody>
                                <tr>
                                    <th>Information</th>
                                </tr>
                                <tr>
                                    <td>The Charminar is situated on the east bank of Musi River.[5] To the west lies the Laad Bazaar, and to the southwest lies the richly ornamented Makkah Masjid.[6] It is listed as an archaeological and architectural treasure on the official list of monuments prepared by the Archaeological Survey of India.[7] The English name is a translation and combination of the Urdu words chār and minar or meenar, translating to "Four Pillars"; the towers are ornate minarets attached and supported by four grand arches
charminar.</td>
                                </tr>
                            </tbody>
                        </table>
                    )}

                    {/* Add other images and information here */}
                    {/* Shiva Image */}
                    <div style={{ marginRight: '20px' }}>
                        <img src={shiva} alt="Shiva" width="300" height="200" onClick={toggleShivaInfo} style={{ cursor: 'pointer' }} />
                        <h3>TAMIL NADU</h3>
                    </div>
                    {showShivaInfo && (
                        <table style={{ border: '3px solid black', backgroundColor: '#FFD700' }}>
                            <tbody>
                                <tr>
                                    <th>Information</th>
                                </tr>
                                <tr>
                                    <td>Adiyogi is located at the Isha Yoga Centre. Its height, 112 ft, symbolizes the 112 possibilities to attain to moksha (liberation) that are mentioned in yogic culture, and also the 112 chakras in the human system.[5][3] A linga named Yogeshwar Linga was consecrated and placed in front of the statue.[6] The Indian Ministry of Tourism has included the statue in its official Incredible India tourism campaign.[7] It is also the venue of a light and sound show about Shiva as a yogi, inaugurated by the President of India, Ram Nath Kovind
adhi yogi.</td>
                                </tr>
                            </tbody>
                        </table>
                    )}

                    {/* Jatayu Image */}
                    <div style={{ marginRight: '20px' }}>
                        <img src={jatayu} alt="Jatayu" width="300" height="200" onClick={toggleJatayuInfo} style={{ cursor: 'pointer' }} />
                        <h3>KERALA</h3>
