// client/src/components/StateDetail.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const StateDetail = () => {
  const { stateId } = useParams();
  const [stateInfo, setStateInfo] = useState({});

  useEffect(() => {
    // Fetch details of a specific state from your backend
    axios.get(`/api/state/${stateId}`)
      .then(response => setStateInfo(response.data))
      .catch(error => console.error('Error fetching state details:', error));
  }, [stateId]);

  const handleButtonClick = () => {
    // Handle button click to display more details
    console.log('Button clicked!');
  };

  return (
    <div>
      <h2>{stateInfo.name} Culture</h2>
      <img src={stateInfo.image} alt={stateInfo.name} />
      <p>{stateInfo.description}</p>
      <button onClick={handleButtonClick}>Show Details</button>
    </div>
  );
};

export default StateDetail;
