import React, { useState } from 'react';
import IndianMapImage from '../images/IndianMap.jpg';

// Define a component for each state button
const StateButton = ({ stateName, onClick }) => {
  return (
    <div
      className="state-button"
      onClick={() => onClick(stateName)}
      style={{ position: 'absolute', ...stateButtonPositions[stateName] }}
    >
      {stateName}
    </div>
  );
};

// Define positions for each state button
const stateButtonPositions = {
  // Add positions for each state button here
  // Example:
  // 'Andhra Pradesh': { top: '10%', left: '20%' },
};

const IndianMap = () => {
  // State to track the selected state
  const [selectedState, setSelectedState] = useState(null);

  // Function to handle state click
  const handleStateClick = (stateName) => {
    setSelectedState(stateName);
  };

  return (
    <div style={{ position: 'relative' }}>
      {/* Render the Indian map image */}
      <img src={IndianMapImage} alt="Indian Map" width="600" height="600" />

      {/* Render state buttons */}
      {Object.keys(stateButtonPositions).map((stateName) => (
        <StateButton key={stateName} stateName={stateName} onClick={handleStateClick} />
      ))}

      {/* Display famous cities of the selected state */}
      {selectedState && (
        <div>
          <h2>Famous Cities of {selectedState}</h2>
          {/* Display famous cities here */}
        </div>
      )}
    </div>
  );
};

export default IndianMap;
