import React, { useState } from 'react';
import IndianMapImage from '../images/indiamap.png';

const IndianMap = () => {
  // State to keep track of the selected state
  const [selectedState, setSelectedState] = useState(null);

  // Function to handle state selection
  const handleStateClick = (state) => {
    setSelectedState(state);
  };

  // Data for famous cities of each state
  const stateCitiesData = {
    // Add data for each state here
    // For example:
    Maharashtra: ["Mumbai", "Pune", "Nagpur"],
    TamilNadu: ["Chennai", "Coimbatore", "Madurai"],
    Karnataka: ["Bangalore", "Mysore", "Hubli"],
    // Add more states and cities as needed
  };

  return (
    <div>
      <img src={IndianMapImage} alt="indiamap" width="500" height="500" useMap="#indiamap" />
      <map name="indiamap">
        {/* Define coordinates for each state */}
        {/* Example: <area shape="poly" coords="x1,y1,x2,y2,..." alt="State" onClick={() => handleStateClick("StateName")} /> */}
        {/* Add coordinates for each state */}
        {/* Example: <area shape="poly" coords="100,200,150,250,200,200" alt="Maharashtra" onClick={() => handleStateClick("Maharashtra")} /> */}
        
        {/* Add area for each state */}
        <area shape="poly" coords="100,200,150,250,200,200" alt="Maharashtra" onClick={() => handleStateClick("Maharashtra")} />
        <area shape="poly" coords="300,300,350,350,400,300" alt="Tamil Nadu" onClick={() => handleStateClick("TamilNadu")} />
        <area shape="poly" coords="200,100,250,150,300,100" alt="Karnataka" onClick={() => handleStateClick("Karnataka")} />
        {/* Add more areas for other states as needed */}
      </map>

      {/* Display selected state's cities */}
      {selectedState && (
        <div>
          <h2>Famous Cities of {selectedState}</h2>
          <ul>
            {stateCitiesData[selectedState].map((city, index) => (
              <li key={index}>{city}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default IndianMap;
