// StateDetails.js

import React from 'react';
import { useParams } from 'react-router-dom';

const StateDetails = ({ stateList }) => {
  const { stateId } = useParams();
  const stateDetails = stateList.find((state) => state._id === parseInt(stateId, 10));

  if (!stateDetails) {
    return <div>State details not found</div>;
  }

  return (
    <div>
      <h2>{stateDetails.name}</h2>
      <h3>Culture</h3>
      <p>{stateDetails.culture}</p>

      <h3>Dress</h3>
      <p>{stateDetails.dress}</p>

      {/* Add more sections for other details like food, festivals, temples, etc. */}
    </div>
  );
};

export default StateDetails;
