import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import tirupati from '../images/tirupati.jpg';
import charminar from '../images/charminar.jpg';
import shiva from '../images/shiva.jpg';
import jatayu from '../images/jatayu.jpg';
import dudhsagarfalls from '../images/dudhsagarfalls.jpg';
import mysorepalace from '../images/mysorepalace.jpg';
import purijagannathtemple from '../images/purijagannathtemple.jpg'; // Puri Jagannath Temple image
import howrah from '../images/howrah.jpg'; // Howrah Bridge image
import gateofindia from '../images/gateofindia.jpg'; // Gateway of India image
import pinkcity from '../images/pinkcity.jpg'; // Pink City (Jaipur) image
import kedarnathtemple from '../images/kedarnathtemple.jpg'; // Kedarnath Temple image
import varanasi from '../images/varanasi.jpg'; // Varanasi image
import redfort from '../images/redfort.jpg'; // Red Fort image
import baltalvalley from '../images/baltalvalley.jpg'; // Baltal Valley image

const HeritageSites = () => {
    const location = useLocation();
    const username = location.state?.username || 'Guest';
    console.log('hello' + username); // Add a semicolon at the end of the console.log statement
    
    // State to toggle information display
    const [showTirupatiInfo, setShowTirupatiInfo] = useState(false);
    const [showCharminarInfo, setShowCharminarInfo] = useState(false);
    const [showShivaInfo, setShowShivaInfo] = useState(false);
    const [showJatayuInfo, setShowJatayuInfo] = useState(false);
    const [showDudhsagarInfo, setShowDudhsagarInfo] = useState(false);
    const [showMysorePalaceInfo, setShowMysorePalaceInfo] = useState(false);
    const [showPuriJagannathInfo, setShowPuriJagannathInfo] = useState(false); // Puri Jagannath Temple info state
    const [showHowrahInfo, setShowHowrahInfo] = useState(false); // Howrah Bridge info state
    const [showGateOfIndiaInfo, setShowGateOfIndiaInfo] = useState(false); // Gateway of India info state
    const [showPinkCityInfo, setShowPinkCityInfo] = useState(false); // Pink City (Jaipur) info state
    const [showKedarnathInfo, setShowKedarnathInfo] = useState(false); // Kedarnath Temple info state
    const [showVaranasiInfo, setShowVaranasiInfo] = useState(false); // Varanasi info state
    const [showRedFortInfo, setShowRedFortInfo] = useState(false); // Red Fort info state
    const [showBaltalValleyInfo, setShowBaltalValleyInfo] = useState(false); // Baltal Valley info state

    // Function to toggle information display for Tirupati
    const toggleTirupatiInfo = () => {
        setShowTirupatiInfo(!showTirupatiInfo);
    };

    // Function to toggle information display for Charminar
    const toggleCharminarInfo = () => {
        setShowCharminarInfo(!showCharminarInfo);
    };

    // Function to toggle information display for Shiva
    const toggleShivaInfo = () => {
        setShowShivaInfo(!showShivaInfo);
    };

    // Function to toggle information display for Jatayu
    const toggleJatayuInfo = () => {
        setShowJatayuInfo(!showJatayuInfo);
    };

    // Function to toggle information display for Dudhsagar Falls
    const toggleDudhsagarInfo = () => {
        setShowDudhsagarInfo(!showDudhsagarInfo);
    };

    // Function to toggle information display for Mysore Palace
    const toggleMysorePalaceInfo = () => {
        setShowMysorePalaceInfo(!showMysorePalaceInfo);
    };

    // Function to toggle information display for Puri Jagannath Temple
    const togglePuriJagannathInfo = () => {
        setShowPuriJagannathInfo(!showPuriJagannathInfo);
    };

    // Function to toggle information display for Howrah Bridge
    const toggleHowrahInfo = () => {
        setShowHowrahInfo(!showHowrahInfo);
    };

    // Function to toggle information display for Gateway of India
    const toggleGateOfIndiaInfo = () => {
        setShowGateOfIndiaInfo(!showGateOfIndiaInfo);
    };

    // Function to toggle information display for Pink City (Jaipur)
    const togglePinkCityInfo = () => {
        setShowPinkCityInfo(!showPinkCityInfo);
    };

    // Function to toggle information display for Kedarnath Temple
    const toggleKedarnathInfo = () => {
        setShowKedarnathInfo(!showKedarnathInfo);
    };

    // Function to toggle information display for Varanasi
    const toggleVaranasiInfo = () => {
        setShowVaranasiInfo(!showVaranasiInfo);
    };

    // Function to toggle information display for Red Fort
    const toggleRedFortInfo = () => {
        setShowRedFortInfo(!showRedFortInfo);
    };

    // Function to toggle information display for Baltal Valley
    const toggleBaltalValleyInfo = () => {
        setShowBaltalValleyInfo(!showBaltalValleyInfo);
    };

    return (
        <div>
            <center>
                <h1>Few heritage sites of India!!</h1>
                
                {/* Display Tirupati image and information */}
                <div onClick={toggleTirupatiInfo} style={{ cursor: 'pointer' }}>
                    <img src={tirupati} alt="Tirupati" width="300" height="200" />
                    <h3>Tirupati</h3>
                </div>
                {showTirupatiInfo && (
                    <div>
                        <h2>Tirupati</h2>
                        <p>Tirupati is a city in the Indian state of Andhra Pradesh. It is known for the famous Tirumala Venkateswara Temple, one of the richest pilgrimage sites in the world.</p>
                    </div>
                )}

                {/* Display Charminar image and information */}
                <div onClick={toggleCharminarInfo} style={{ cursor: 'pointer' }}>
                    <img src={charminar} alt="Charminar" width="300" height="200" />
                    <h3>Charminar</h3>
                </div>
                {showCharminarInfo && (
                    <div>
                        <h2>Charminar</h2>
                        <p>Charminar is a city in the Indian state of Telangana. It is known for the famous Charminar monument, which was built in 1591 and is a symbol of the city's history and culture.</p>
                    </div>
                )}

                {/* Display Shiva image and information */}
                <div onClick={toggleShivaInfo} style={{ cursor: 'pointer' }}>
                    <img src={shiva} alt="Shiva" width="300" height="200" />
                    <h3>Adiyogi Shiva</h3>
                </div>
                {showShivaInfo && (
                    <div>
                        <h2>Adiyogi Shiva</h2>
                        <p>The Adiyogi Shiva bust is a 34-metre tall (112 ft), 45-metre long (147 ft), and 25-metre wide (82 ft) steel bust of Shiva with Thirunamam at Coimbatore, Tamil Nadu. It is recognized by the Guinness World Records as the "Largest Bust Sculpture” in the world.</p>
                    </div>
                )}

                {/* Display Jatayu image and information */}
                <div onClick={toggleJatayuInfo} style={{ cursor: 'pointer' }}>
                    <img src={jatayu} alt="Jatayu" width="300" height="200" />
                    <h3>Jatayu Statue</h3>
                </div>
                {showJatayuInfo && (
                    <div>
                        <h2>Jatayu Statue</h2>
                        <p>Jatayu is a demigod in the Hindu epic Ramayana, who has the form of either an eagle or a vulture. He was the younger son of Aruṇa and his wife Shyeni, the brother of Sampati, as well as the nephew of Garuda. He was also an old friend of King Dasharatha, Rama's father.</p>
                    </div>
                )}

                {/* Display Dudhsagar Falls image and information */}
                <div onClick={toggleDudhsagarInfo} style={{ cursor: 'pointer' }}>
                    <img src={dudhsagarfalls} alt="Dudhsagar Falls" width="300" height="200" />
                    <h3>Dudhsagar Falls</h3>
                </div>
                {showDudhsagarInfo && (
                    <div>
                        <h2>Dudhsagar Falls</h2>
                        <p>Dudhsagar Falls is a four-tiered waterfall located on the Mandovi River in the Indian state of Goa. It is one of the tallest waterfalls in India and is a popular tourist attraction, especially during the monsoon season.</p>
                    </div>
                )}

                {/* Display Mysore Palace image and information */}
                <div onClick={toggleMysorePalaceInfo} style={{ cursor: 'pointer' }}>
                    <img src={mysorepalace} alt="Mysore Palace" width="300" height="200" />
                    <h3>Mysore Palace</h3>
                </div>
                {showMysorePalaceInfo && (
                    <div>
                        <h2>Mysore Palace</h2>
                        <p>Mysore Palace, also known as the Amba Vilas Palace, is a historical palace located in the city of Mysore in Karnataka, India. It is one of the most famous tourist attractions in Mysore and is known for its grand architecture and intricate designs.</p>
                    </div>
                )}

                {/* Display Puri Jagannath Temple image and information */}
                <div onClick={togglePuriJagannathInfo} style={{ cursor: 'pointer' }}>
                    <img src={purijagannathtemple} alt="Puri Jagannath Temple" width="300" height="200" />
                    <h3>Puri Jagannath Temple</h3>
                </div>
                {showPuriJagannathInfo && (
                    <div>
                        <h2>Puri Jagannath Temple</h2>
                        <p>The Jagannath Temple of Puri is a famous, sacred Hindu temple dedicated to Jagannath and located on the eastern coast of India, in the state of Odisha.</p>
                    </div>
                )}

                {/* Display Howrah Bridge image and information */}
                <div onClick={toggleHowrahInfo} style={{ cursor: 'pointer' }}>
                    <img src={howrah} alt="Howrah Bridge" width="300" height="200" />
                    <h3>Howrah Bridge</h3>
                </div>
                {showHowrahInfo && (
                    <div>
                        <h2>Howrah Bridge</h2>
                        <p>Howrah Bridge is a cantilever bridge with a suspended span over the Hooghly River in Kolkata (Calcutta), West Bengal, India.</p>
                    </div>
                )}

                {/* Display Gateway of India image and information */}
                <div onClick={toggleGateOfIndiaInfo} style={{ cursor: 'pointer' }}>
                    <img src={gateofindia} alt="Gateway of India" width="300" height="200" />
                    <h3>Gateway of India</h3>
                </div>
                {showGateOfIndiaInfo && (
                    <div>
                        <h2>Gateway of India</h2>
                        <p>The Gateway of India is an arch-monument built in the early twentieth century in the city of Mumbai, in the Indian state of Maharashtra.</p>
                    </div>
                )}

                {/* Display Pink City image and information */}
                <div onClick={togglePinkCityInfo} style={{ cursor: 'pointer' }}>
                    <img src={pinkcity} alt="Pink City" width="300" height="200" />
                    <h3>Pink City (Jaipur)</h3>
                </div>
                {showPinkCityInfo && (
                    <div>
                        <h2>Pink City (Jaipur)</h2>
                        <p>Jaipur is known as the Pink City due to the distinctive saffron or pink color of its buildings. The city, painted in pink, offers splendid views at sunrise and sunset and keeps visitors intrigued by its various art and crafts.</p>
                    </div>
                )}

                {/* Display Kedarnath Temple image and information */}
                <div onClick={toggleKedarnathInfo} style={{ cursor: 'pointer' }}>
                    <img src={kedarnathtemple} alt="Kedarnath Temple" width="300" height="200" />
                    <h3>Kedarnath Temple</h3>
                </div>
                {showKedarnathInfo && (
                    <div>
                        <h2>Kedarnath Temple</h2>
                        <p>Kedarnath Temple is a Hindu temple dedicated to Lord Shiva. It is located on the Garhwal Himalayan range near the Mandakini river in Kedarnath, Uttarakhand, India.</p>
                    </div>
                )}

                {/* Display Varanasi image and information */}
                <div onClick={toggleVaranasiInfo} style={{ cursor: 'pointer' }}>
                    <img src={varanasi} alt="Varanasi" width="300" height="200" />
                    <h3>Varanasi</h3>
                </div>
                {showVaranasiInfo && (
                    <div>
                        <h2>Varanasi</h2>
                        <p>Varanasi, also known as Banaras or Kashi, is a city on the banks of the Ganges in Uttar Pradesh, India. It is one of the oldest continuously inhabited cities in the world and is considered the spiritual capital of India.</p>
                    </div>
                )}

                {/* Display Red Fort image and information */}
                <div onClick={toggleRedFortInfo} style={{ cursor: 'pointer' }}>
                    <img src={redfort} alt="Red Fort" width="300" height="200" />
                    <h3>Red Fort</h3>
                </div>
                {showRedFortInfo && (
                    <div>
                        <h2>Red Fort</h2>
                        <p>The Red Fort is a historic fort in the city of Delhi in India. It was the main residence of the emperors of the Mughal dynasty for nearly 200 years, until 1857.</p>
                    </div>
                )}

                {/* Display Baltal Valley image and information */}
                <div onClick={toggleBaltalValleyInfo} style={{ cursor: 'pointer' }}>
                    <img src={baltalvalley} alt="Baltal Valley" width="300" height="200" />
                    <h3>Baltal Valley</h3>
                </div>
                {showBaltalValleyInfo && (
                    <div>
                        <h2>Baltal Valley</h2>
                        <p>Baltal Valley is a lush green landscape in the northern Indian state of Jammu and Kashmir. It is known for its scenic beauty and is a base camp for the annual Amarnath Yatra pilgrimage.</p>
                    </div>
                )}
            </center>
        </div>
    );
};

export default HeritageSites;
