import React, { useState } from 'react';
import IndianMapImage from '../images/indiamap.png';

const IndianMap = () => {
  // State to keep track of the selected state
  const [selectedState, setSelectedState] = useState(null);

  // Data for famous cities and locations of each state
  const stateData = {
    AndhraPradesh: {
      cities: ["Visakhapatnam", "Vijayawada", "Tirupati", "Rajamahendravaram"],
      locations: ["Araku Valley", "Tirumala Venkateswara Temple", "Kailasagiri"]
    },
    ArunachalPradesh: {
      cities: ["Itanagar", "Tawang", "Ziro"],
      locations: ["Tawang Monastery", "Sela Pass", "Nuranang Falls"]
    },
    Assam: {
      cities: ["Guwahati", "Silchar", "Dibrugarh"],
      locations: ["Kaziranga National Park", "Kamakhya Temple", "Manas National Park"]
    },
    // Add data for other states
    // ...
  };

  // Function to handle state selection
  const handleStateClick = (state) => {
    setSelectedState(state);
  };

  return (
    <div style={{ textAlign: 'center' }}>
      <img src={IndianMapImage} alt="indiamap" width="500" height="500" useMap="#indiamap" />
      <h2>States of India</h2>
      <table style={{ margin: '20px auto', padding: '10px', border: '1px solid black', borderCollapse: 'collapse' }}>
        <tbody>
          {/* Add rows for each state */}
          {Object.keys(stateData).map((state, index) => (
            <tr key={index}>
              <td style={{ border: '1px solid black' }}>
                <button onClick={() => handleStateClick(state)} style={{ backgroundColor: 'lightblue', padding: '5px 10px', margin: '5px', border: 'none', borderRadius: '5px' }}>
                  {state}
                </button>
              </td>
              {/* Add cells for famous locations */}
              <td style={{ border: '1px solid black' }}>
                <ul>
                  {stateData[state].locations.map((location, index) => (
                    <li key={index}>{location}</li>
                  ))}
                </ul>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Display selected state's cities */}
      {selectedState && (
        <div>
          <h2>Famous Cities of {selectedState}</h2>
          <table style={{ margin: '20px auto', padding: '10px', border: '1px solid black', borderCollapse: 'collapse' }}>
            <tbody>
              {/* Add rows for cities */}
              {stateData[selectedState].cities.map((city, index) => (
                <tr key={index}>
                  <td style={{ border: '1px solid black' }}>{city}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default IndianMap;
