import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import tirupati from '../images/tirupati.jpg'; // Adjust the image path as needed

const HeritageSites = () => {
    const location = useLocation();
    const username = location.state?.username || 'Guest';
    console.log('hello' + username); // Add a semicolon at the end of the console.log statement
    
    // State to toggle information display
    const [showInfo, setShowInfo] = useState(false);

    // Function to toggle information display
    const toggleInfo = () => {
        setShowInfo(!showInfo);
    };

    return (
        <div>
            {/* <h2>Welcome, {username}!</h2> */}
            {/* other content of the home page*/}
            <center>
                <h1>Few heritage sites of India!!</h1>
                {/* Wrap the image in a clickable element */}
                <div onClick={toggleInfo} style={{ cursor: 'pointer' }}>
                    <img src={tirupati} alt="Heritage Sites" width="400" height="300" /> <h1>ap</h1>{/* Adjust image attributes as needed */}
                </div>
                {/* Display information if showInfo is true */}
                {showInfo && (
                    <div>
                        <h2>Tirupati</h2>
                        <p>Tirupati is a city in the Indian state of Andhra Pradesh. It is known for the famous Tirumala Venkateswara Temple, one of the richest pilgrimage sites in the world.</p>
                        {/* Add more information as needed */}
                    </div>
                )}
            </center>
        </div>
    );
};

export default HeritageSites;
