import React, { useState } from 'react';
import bharat from '../images/bharat.jpg'; // Import image for dances
import foodImage from '../images/food.jpg'; // Import image for food
import artifactsImage from '../images/artifacts.jpg'; // Import image for artifacts
import festivalsImage from '../images/festivals.jpg'; // Import image for festivals
import musicImage from '../images/music.jpg'; // Import image for music

const Module = () => {
  const [showDances, setShowDances] = useState(false);
  const [showFood, setShowFood] = useState(false);
  const [showArtifacts, setShowArtifacts] = useState(false);
  const [showFestivals, setShowFestivals] = useState(false);
  const [showMusic, setShowMusic] = useState(false);

  const dances = [
    { name: 'Bharatnatyam', description: 'Classical dance form originating from Tamil Nadu', image: bharat },
    // Add images for other dances...
  ];

  const food = [
    { name: 'Pongal', description: 'Tamil harvest festival', image: foodImage },
    // Add images for other food items...
  ];

  const artifacts = [
    { name: 'Buddhist Artifacts', description: 'Artifacts related to Buddhism, including statues, relics, and scriptures', image: artifactsImage },
    // Add images for other artifacts...
  ];

  const festivals = [
    { name: 'Holi', description: 'Festival of colors and love', image: festivalsImage },
    // Add images for other festivals...
  ];

  const music = [
    { name: 'Indian Pop Music', description: 'Popular music genre in India blending Indian and Western styles', image: musicImage },
    // Add images for other music genres...
  ];

  const handleDancesClick = () => {
    setShowDances(true);
    setShowFood(false);
    setShowArtifacts(false);
    setShowFestivals(false);
    setShowMusic(false);
  };

  const handleFoodClick = () => {
    setShowDances(false);
    setShowFood(true);
    setShowArtifacts(false);
    setShowFestivals(false);
    setShowMusic(false);
  };

  const handleArtifactsClick = () => {
    setShowDances(false);
    setShowFood(false);
    setShowArtifacts(true);
    setShowFestivals(false);
    setShowMusic(false);
  };

  const handleFestivalsClick = () => {
    setShowDances(false);
    setShowFood(false);
    setShowArtifacts(false);
    setShowFestivals(true);
    setShowMusic(false);
  };

  const handleMusicClick = () => {
    setShowDances(false);
    setShowFood(false);
    setShowArtifacts(false);
    setShowFestivals(false);
    setShowMusic(true);
  };

  return (
    <div className="module-container">
      <center>
        <div className="module-options">
          <table>
            <tbody>
              <tr>
                <td>
                  <span onClick={handleDancesClick} className={`module-option ${showDances ? 'active' : ''}`}>
                    Dances
                  </span>
                </td>
                <td>
                  <span onClick={handleFoodClick} className={`module-option ${showFood ? 'active' : ''}`}>
                    Food
                  </span>
                </td>
                <td>
                  <span onClick={handleArtifactsClick} className={`module-option ${showArtifacts ? 'active' : ''}`}>
                    Artifacts
                  </span>
                </td>
                <td>
                  <span onClick={handleFestivalsClick} className={`module-option ${showFestivals ? 'active' : ''}`}>
                    Festivals
                  </span>
                </td>
                <td>
                  <span onClick={handleMusicClick} className={`module-option ${showMusic ? 'active' : ''}`}>
                    Music
                  </span>
                </td>
              </tr>
            </tbody>
          </table>
        </div>
      </center>
      <div className="module-details">
        {showDances && (
          <div className="module-card">
            <img src={dances[0].image} alt="Bharatnatyam" />
            <h3>Dances</h3>
            <table className="module-table">
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Description</th>
                </tr>
              </thead>
              <tbody>
                {dances.map((dance, index) => (
                  <tr key={index}>
                    <td>{dance.name}</td>
                    <td>{dance.description}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
        {showFood && (
          <div className="module-card">
            <img src={food[0].image} alt="Pongal" />
            <h3>Food</h3>
            <table className="module-table">
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Description</th>
                </tr>
              </thead>
              <tbody>
                {food.map((item, index) => (
                  <tr key={index}>
                    <td>{item.name}</td>
                    <td>{item.description}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
        {showArtifacts && (
          <div className="module-card">
            <img src={artifacts[0].image} alt="Buddhist Artifacts" />
            <h3>Artifacts</h3>
            <table className="module-table">
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Description</th>
                </tr>
              </thead>
              <tbody>
                {artifacts.map((artifact, index) => (
                  <tr key={index}>
                    <td>{artifact.name}</td>
                    <td>{artifact.description}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
        {showFestivals && (
          <div className="module-card">
            <img src={festivals[0].image} alt="Holi" />
            <h3>Festivals</h3>
            <table className="module-table">
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Description</th>
                </tr>
              </thead>
              <tbody>
                {festivals.map((festival, index) => (
                  <tr key={index}>
                    <td>{festival.name}</td>
                    <td>{festival.description}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
        {showMusic && (
          <div className="module-card">
            <img src={music[0].image} alt="Indian Pop Music" />
            <h3>Music</h3>
            <table className="module-table">
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Description</th>
                </tr>
              </thead>
              <tbody>
                {music.map((genre, index) => (
                  <tr key={index}>
                    <td>{genre.name}</td>
                    <td>{genre.description}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default Module;
