import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import tirupati from '../images/tirupati.jpg';
import charminar from '../images/charminar.jpg';
import shiva from '../images/shiva.jpg';
import jatayu from '../images/jatayu.jpg';
import dudhsagarfalls from '../images/dudhsagarfalls.jpg';
import mysorepalace from '../images/mysorepalace.jpg';
import purijagannathtemple from '../images/purijagannathtemple.jpg';
import howrah from '../images/howrah.jpg';
import gateofindia from '../images/gateofindia.jpg';
import pinkcity from '../images/pinkcity.jpg';
import kedarnathtemple from '../images/kedarnathtemple.jpg';
import varanasi from '../images/varanasi.jpg';
import redfort from '../images/redfort.jpg';
import baltalvalley from '../images/baltalvalley.jpg';

const HeritageSites = () => {
    const location = useLocation();
    const username = location.state?.username || 'Guest';

    // State for toggling information display
    const [showTirupatiInfo, setShowTirupatiInfo] = useState(false);
    const [showCharminarInfo, setShowCharminarInfo] = useState(false);
    const [showShivaInfo, setShowShivaInfo] = useState(false);
    const [showJatayuInfo, setShowJatayuInfo] = useState(false);
    const [showDudhsagarInfo, setShowDudhsagarInfo] = useState(false);
    const [showMysorePalaceInfo, setShowMysorePalaceInfo] = useState(false);
    const [showPuriJagannathInfo, setShowPuriJagannathInfo] = useState(false);
    const [showHowrahInfo, setShowHowrahInfo] = useState(false);
    const [showGateOfIndiaInfo, setShowGateOfIndiaInfo] = useState(false);
    const [showPinkCityInfo, setShowPinkCityInfo] = useState(false);
    const [showKedarnathInfo, setShowKedarnathInfo] = useState(false);
    const [showVaranasiInfo, setShowVaranasiInfo] = useState(false);
    const [showRedFortInfo, setShowRedFortInfo] = useState(false);
    const [showBaltalValleyInfo, setShowBaltalValleyInfo] = useState(false);

    // Function to toggle information display for Tirupati
    const toggleTirupatiInfo = () => {
        setShowTirupatiInfo(!showTirupatiInfo);
    };

    // Function to toggle information display for Charminar
    const toggleCharminarInfo = () => {
        setShowCharminarInfo(!showCharminarInfo);
    };

    // Function to toggle information display for Shiva
    const toggleShivaInfo = () => {
        setShowShivaInfo(!showShivaInfo);
    };

    // Function to toggle information display for Jatayu
    const toggleJatayuInfo = () => {
        setShowJatayuInfo(!showJatayuInfo);
    };

    // Function to toggle information display for Dudhsagar Falls
    const toggleDudhsagarInfo = () => {
        setShowDudhsagarInfo(!showDudhsagarInfo);
    };

    // Function to toggle information display for Mysore Palace
    const toggleMysorePalaceInfo = () => {
        setShowMysorePalaceInfo(!showMysorePalaceInfo);
    };

    // Function to toggle information display for Puri Jagannath Temple
    const togglePuriJagannathInfo = () => {
        setShowPuriJagannathInfo(!showPuriJagannathInfo);
    };

    // Function to toggle information display for Howrah
    const toggleHowrahInfo = () => {
        setShowHowrahInfo(!showHowrahInfo);
    };

    // Function to toggle information display for Gate of India
    const toggleGateOfIndiaInfo = () => {
        setShowGateOfIndiaInfo(!showGateOfIndiaInfo);
    };

    // Function to toggle information display for Pink City
    const togglePinkCityInfo = () => {
        setShowPinkCityInfo(!showPinkCityInfo);
    };

    // Function to toggle information display for Kedarnath Temple
    const toggleKedarnathInfo = () => {
        setShowKedarnathInfo(!showKedarnathInfo);
    };

    // Function to toggle information display for Varanasi
    const toggleVaranasiInfo = () => {
        setShowVaranasiInfo(!showVaranasiInfo);
    };

    // Function to toggle information display for Red Fort
    const toggleRedFortInfo = () => {
        setShowRedFortInfo(!showRedFortInfo);
    };

    // Function to toggle information display for Baltal Valley
    const toggleBaltalValleyInfo = () => {
        setShowBaltalValleyInfo(!showBaltalValleyInfo);
    };

    return (
        <div>
            <center>
                <h1>Few heritage sites of India!!</h1>
                
                <div style={{ display: 'flex', flexDirection: 'row', justifyContent: 'center', flexWrap: 'wrap' }}>
                    {/* Tirupati Image */}
                    <div style={{ marginRight: '20px' }}>
                        <img src={tirupati} alt="Tirupati" width="300" height="200" onClick={toggleTirupatiInfo} style={{ cursor: 'pointer' }} />
                        <h3 >Tirupati</h3>
                    </div>
                    {showTirupatiInfo && (
                        <table style={{ border: '1px solid black', backgroundColor: '#FFE4E1' }}>
                            <tbody>
                                <tr>
                                    <th>Information</th>
                                </tr>
                                <tr>
                                    <td>Tirupati is a city in the Indian state of Andhra Pradesh. It is known for the famous Tirumala Venkateswara Temple, one of the richest pilgrimage sites in the world.</td>
                                </tr>
                            </tbody>
                        </table>
                    )}

                    {/* Charminar Image */}
                    <div style={{ marginRight: '20px' }}>
                        <img src={charminar} alt="Charminar" width="300" height="200" onClick={toggleCharminarInfo} style={{ cursor: 'pointer' }} />
                        <h3>Charminar</h3>
                    </div>
                    {showCharminarInfo && (
                        <table style={{ border: '1px solid black', backgroundColor: '#ADD8E6' }}>
                            <tbody>
                                <tr>
                                    <th>Information</th>
                                </tr>
                                <tr>
                                    <td>Charminar is a city in the Indian state of Telangana. It is known for the famous Charminar monument, which was built in 1591 and is a symbol of the city's history and culture.</td>
                                </tr>
                            </tbody>
                        </table>
                    )}

                    {/* Add other images and information here */}
                    {/* Shiva Image */}
                    <div style={{ marginRight: '20px' }}>
                        <img src={shiva} alt="Shiva" width="300" height="200" onClick={toggleShivaInfo} style={{ cursor: 'pointer' }} />
                        <h3>Adiyogi Shiva</h3>
                    </div>
                    {showShivaInfo && (
                        <table style={{ border: '1px solid black', backgroundColor: '#FFD700' }}>
                            <tbody>
                                <tr>
                                    <th>Information</th>
                                </tr>
                                <tr>
                                    <td>The Adiyogi Shiva bust is a 34-metre tall (112 ft), 45-metre long (147 ft) and 25-metre wide (82 ft) steel bust of Shiva with Thirunamam at Coimbatore, Tamil Nadu. It is recognized by the Guinness World Records as the "Largest Bust Sculpture in the world.</td>
                                </tr>
                            </tbody>
                        </table>
                    )}

                    {/* Jatayu Image */}
                    <div style={{ marginRight: '20px' }}>
                        <img src={jatayu} alt="Jatayu" width="300" height="200" onClick={toggleJatayuInfo} style={{ cursor: 'pointer' }} />
                        <h3>Jatayu Statue</h3>
                    </div>
                    {showJatayuInfo && (
                        <table style={{ border: '1px solid black', backgroundColor: '#90EE90' }}>
                            <tbody>
                                <tr>
                                    <th>Information</th>
                                </tr>
                                <tr>
                                    <td>Jatayu is a demigod in the Hindu epic Ramayana, who has the form of either an eagle or a vulture. He was the younger son of Arua and his wife Shyeni, the brother of Sampati, as well as the nephew of Garuda. He was also an old friend of King Dasharatha, Rama's father.</td>
                                </tr>
                            </tbody>
                        </table>
                    )}

                    {/* Dudhsagar Falls Image */}
                    <div style={{ marginRight: '20px' }}>
                        <img src={dudhsagarfalls} alt="Dudhsagar Falls" width="300" height="200" onClick={toggleDudhsagarInfo} style={{ cursor: 'pointer' }} />
                        <h3>Dudhsagar Falls</h3>
                    </div>
                    {showDudhsagarInfo && (
                        <table style={{ border: '1px solid black', backgroundColor: '#FFA07A' }}>
                            <tbody>
                                <tr>
                                    <th>Information</th>
                                </tr>
                                <tr>
                                    <td>Dudhsagar Falls is a four-tiered waterfall located on the Mandovi River in the Indian state of Goa. It is one of the tallest waterfalls in India and is a popular tourist attraction, especially during the monsoon season.</td>
                                </tr>
                            </tbody>
                        </table>
                    )}

                    {/* Mysore Palace Image */}
                    <div style={{ marginRight: '20px' }}>
                        <img src={mysorepalace} alt="Mysore Palace" width="300" height="200" onClick={toggleMysorePalaceInfo} style={{ cursor: 'pointer' }} />
                        <h3>Mysore Palace</h3>
                    </div>
                    {showMysorePalaceInfo && (
                        <table style={{ border: '1px solid black', backgroundColor: '#D8BFD8' }}>
                            <tbody>
                                <tr>
                                    <th>Information</th>
                                </tr>
                                <tr>
                                    <td>Mysore Palace, also known as the Amba Vilas Palace, is a historical palace located in the city of Mysore in Karnataka, India. It is one of the most famous tourist attractions in Mysore and is known for its grand architecture and intricate designs.</td>
                                </tr>
                            </tbody>
                        </table>
                    )}

                    {/* Puri Jagannath Temple Image */}
                    <div style={{ marginRight: '20px' }}>
                        <img src={purijagannathtemple} alt="Puri Jagannath Temple" width="300" height="200" onClick={togglePuriJagannathInfo} style={{ cursor: 'pointer' }} />
                        <h3>Puri Jagannath Temple</h3>
                    </div>
                    {showPuriJagannathInfo && (
                        <table style={{ border: '1px solid black', backgroundColor: '#FFB6C1' }}>
                            <tbody>
                                <tr>
                                    <th>Information</th>
                                </tr>
                                <tr>
                                    <td>The Jagannath Temple in Puri is a famous Hindu temple dedicated to Lord Jagannath, a form of Lord Vishnu. It is located in the coastal town of Puri in the state of Odisha, India.</td>
                                </tr>
                            </tbody>
                        </table>
                    )}

                    {/* Howrah Image */}
                    <div style={{ marginRight: '20px' }}>
                        <img src={howrah} alt="Howrah" width="300" height="200" onClick={toggleHowrahInfo} style={{ cursor: 'pointer' }} />
                        <h3>Howrah</h3>
                    </div>
                    {showHowrahInfo && (
                        <table style={{ border: '1px solid black', backgroundColor: '#87CEFA' }}>
                            <tbody>
                                <tr>
                                    <th>Information</th>
                                </tr>
                                <tr>
                                    <td>Howrah is a city located in the Indian state of West Bengal. It is known for the Howrah Bridge, one of the most famous landmarks in Kolkata.</td>
                                </tr>
                            </tbody>
                        </table>
                    )}

                    {/* Gate of India Image */}
                    <div style={{ marginRight: '20px' }}>
                        <img src={gateofindia} alt="Gate of India" width="300" height="200" onClick={toggleGateOfIndiaInfo} style={{ cursor: 'pointer' }} />
                        <h3>Gate of India</h3>
                    </div>
                    {showGateOfIndiaInfo && (
                        <table style={{ border: '1px solid black', backgroundColor: '#00FF7F' }}>
                            <tbody>
                                <tr>
                                    <th>Information</th>
                                </tr>
                                <tr>
                                    <td>The Gateway of India is an arch-monument built in the early twentieth century in the city of Mumbai, Maharashtra. It is located on the waterfront in the Apollo Bunder area.</td>
                                </tr>
                            </tbody>
                        </table>
                    )}

                    {/* Pink City Image */}
                    <div style={{ marginRight: '20px' }}>
                        <img src={pinkcity} alt="Pink City" width="300" height="200" onClick={togglePinkCityInfo} style={{ cursor: 'pointer' }} />
                        <h3>Pink City</h3>
                    </div>
                    {showPinkCityInfo && (
                        <table style={{ border: '1px solid black', backgroundColor: '#FF69B4' }}>
                            <tbody>
                                <tr>
                                    <th>Information</th>
                                </tr>
                                <tr>
                                    <td>Jaipur, the capital city of the Indian state of Rajasthan, is famously known as the Pink City due to the distinct color of its buildings. It is a popular tourist destination known for its rich history, culture, and architecture.</td>
                                </tr>
                            </tbody>
                        </table>
                    )}

                    {/* Kedarnath Temple Image */}
                    <div style={{ marginRight: '20px' }}>
                        <img src={kedarnathtemple} alt="Kedarnath Temple" width="300" height="200" onClick={toggleKedarnathInfo} style={{ cursor: 'pointer' }} />
                        <h3>Kedarnath Temple</h3>
                    </div>
                    {showKedarnathInfo && (
                        <table style={{ border: '1px solid black', backgroundColor: '#FFDAB9' }}>
                            <tbody>
                                <tr>
                                    <th>Information</th>
                                </tr>
                                <tr>
                                    <td>Kedarnath Temple is a Hindu temple dedicated to Lord Shiva and is one of the 12 Jyotirlingas, the holiest Shiva temples. It is located in the Garhwal Himalayan range near the Mandakini River in the state of Uttarakhand, India.</td>
                                </tr>
                            </tbody>
                        </table>
                    )}

                    {/* Varanasi Image */}
                    <div style={{ marginRight: '20px' }}>
                        <img src={varanasi} alt="Varanasi" width="300" height="200" onClick={toggleVaranasiInfo} style={{ cursor: 'pointer' }} />
                        <h3>Varanasi</h3>
                    </div>
                    {showVaranasiInfo && (
                        <table style={{ border: '1px solid black', backgroundColor: '#FFFF00' }}>
                            <tbody>
                                <tr>
                                    <th>Information</th>
                                </tr>
                                <tr>
                                    <td>Varanasi, also known as Kashi and Banaras, is one of the oldest continuously inhabited cities in the world. It is a major religious hub in India and is considered one of the holiest cities in Hinduism.</td>
                                </tr>
                            </tbody>
                        </table>
                    )}

                    {/* Red Fort Image */}
                    <div style={{ marginRight: '20px' }}>
                        <img src={redfort} alt="Red Fort" width="300" height="200" onClick={toggleRedFortInfo} style={{ cursor: 'pointer' }} />
                        <h3>Red Fort</h3>
                    </div>
                    {showRedFortInfo && (
                        <table style={{ border: '1px solid black', backgroundColor: '#FF0000' }}>
                            <tbody>
                                <tr>
                                    <th>Information</th>
                                </tr>
                                <tr>
                                    <td>The Red Fort is a historic fort in the city of Delhi, India. It was the main residence of the emperors of the Mughal dynasty for nearly 200 years, until 1857.</td>
                                </tr>
                            </tbody>
                        </table>
                    )}

                    {/* Baltal Valley Image */}
                    <div style={{ marginRight: '20px' }}>
                        <img src={baltalvalley} alt="Baltal Valley" width="300" height="200" onClick={toggleBaltalValleyInfo} style={{ cursor: 'pointer' }} />
                        <h3>Baltal Valley</h3>
                    </div>
                    {showBaltalValleyInfo && (
                        <table style={{ border: '1px solid black', backgroundColor: '#D3D3D3' }}>
                            <tbody>
                                <tr>
                                    <th>Information</th>
                                </tr>
                                <tr>
                                    <td>The Baltal Valley is a lush green valley located near the Amarnath Cave in the Himalayas of Jammu and Kashmir. It serves as a base camp for the annual Amarnath Yatra pilgrimage.</td>
                                </tr>
                            </tbody>
                        </table>
                    )}
                </div>
            </center>
        </div>
    );
};

export default HeritageSites;
