// StateDetails.js

import React from 'react';
import { useParams } from 'react-router-dom';

const StateDetails = ({ stateList }) => {
  const { stateId } = useParams();

  // Find the state with the matching _id
  const selectedState = stateList.find(state => state._id.toString() === stateId);

  if (!selectedState) {
    return <div>State not found</div>;
  }

  return (
    <div>
      <h2>{selectedState.name} Details</h2>
      <p>Culture: {selectedState.culture}</p>
      <p>Dress: {selectedState.dress}</p>
      <p>Food: {selectedState.food}</p>
      <p>Festivals: {selectedState.festivals}</p>
      <p>Temples: {selectedState.temples}</p>
    </div>
  );
};

export default StateDetails;
