import React, { useState } from 'react';
import './Module.css'; // Import the CSS file

const Module = () => {
  const [showDances, setShowDances] = useState(false);
  const [showFood, setShowFood] = useState(false);
  const [showArtifacts, setShowArtifacts] = useState(false);
  const [showFestivals, setShowFestivals] = useState(false);
  const [showMusic, setShowMusic] = useState(false);

  const dances = [
    { name: 'Bharatnatyam', description: 'Classical dance form originating from Tamil Nadu' },
    { name: 'Kathak', description: 'Classical dance form originating from North India, known for its intricate footwork and graceful movements' },
    { name: 'Kathakali', description: 'Classical dance-drama from Kerala, known for its elaborate costumes and makeup' },
    { name: 'Manipuri', description: 'Classical dance form from Manipur, characterized by its graceful and fluid movements' },
    { name: 'Kuchipudi', description: 'Classical dance form from Andhra Pradesh, combining dance, music, and acting' },
    { name: 'Odissi', description: 'Classical dance form from Odisha, characterized by fluid movements and sculpturesque poses' },
  ];

  const food = [
    { name: 'Pongal', description: 'Tamil harvest festival' },
    { name: 'Pootha Rekulu', description: 'Traditional sweet from Andhra Pradesh' },
    { name: 'Hyderabadi Biryani', description: 'Famous rice dish from Hyderabad, Telangana' },
    { name: 'Appam', description: 'South Indian pancake made with fermented rice batter and coconut milk' },
    { name: 'Sambar Idly', description: 'South Indian breakfast dish consisting of idlis served with sambar' },
  ];

  const artifacts = [
    { name: 'Buddhist Artifacts', description: 'Artifacts related to Buddhism, including statues, relics, and scriptures' },
    { name: 'Mughal Artifacts', description: 'Artifacts from the Mughal era, including architecture, paintings, and artifacts' },
    { name: 'Colonial-Era Artifacts', description: 'Artifacts from the colonial period in India, reflecting European influence' },
    { name: 'Textiles and Handicrafts', description: 'Traditional textiles and handicrafts from different regions of India' },
    { name: 'Folk Art and Crafts', description: 'Art and crafts representing the cultural heritage of various communities in India' },
  ];

  const festivals = [
    { name: 'Holi', description: 'Festival of colors and love' },
    { name: 'Dussehra', description: 'Celebration of the victory of good over evil' },
    { name: 'Pongal', description: 'Tamil harvest festival' },
    { name: 'Diwali', description: 'Festival of lights celebrated across India' },
    { name: 'Sri Ramanavami', description: 'Celebration of the birth of Lord Rama' },
  ];

  const music = [
    { name: 'Indian Pop Music', description: 'Popular music genre in India blending Indian and Western styles' },
    { name: 'Classical Music', description: 'Traditional music genre with deep roots in Indian culture' },
    { name: 'Folk Music', description: 'Music reflecting the cultural traditions of different regions in India' },
    { name: 'Tabla', description: 'Indian percussion instrument commonly used in classical, devotional, and popular music' },
    { name: 'Bhangra', description: 'Punjabi folk dance and music genre known for its energetic beats' },
  ];

  const handleDancesClick = () => {
    setShowDances(true);
    setShowFood(false);
    setShowArtifacts(false);
    setShowFestivals(false);
    setShowMusic(false);
  };

  const handleFoodClick = () => {
    setShowDances(false);
    setShowFood(true);
    setShowArtifacts(false);
    setShowFestivals(false);
    setShowMusic(false);
  };

  const handleArtifactsClick = () => {
    setShowDances(false);
    setShowFood(false);
    setShowArtifacts(true);
    setShowFestivals(false);
    setShowMusic(false);
  };

  const handleFestivalsClick = () => {
    setShowDances(false);
    setShowFood(false);
    setShowArtifacts(false);
    setShowFestivals(true);
    setShowMusic(false);
  };

  const handleMusicClick = () => {
    setShowDances(false);
    setShowFood(false);
    setShowArtifacts(false);
    setShowFestivals(false);
    setShowMusic(true);
  };

  return (
    <div className="module-container">
      <center>
        <div className="module-options">
          <table>
            <tbody>
              <tr>
                <td>
                  <span onClick={handleDancesClick} className={`module-option ${showDances ? 'active' : ''}`}>
                    Dances
                  </span>
                </td>
                <td>
                  <span onClick={handleFoodClick} className={`module-option ${showFood ? 'active' : ''}`}>
                    Food
                  </span>
                </td>
                <td>
                  <span onClick={handleArtifactsClick} className={`module-option ${showArtifacts ? 'active' : ''}`}>
                    Artifacts
                  </span>
                </td>
                <td>
                  <span onClick={handleFestivalsClick} className={`module-option ${showFestivals ? 'active' : ''}`}>
                    Festivals
                  </span>
                </td>
                <td>
                  <span onClick={handleMusicClick} className={`module-option ${showMusic ? 'active' : ''}`}>
                    Music
                  </span>
                </td>
              </tr>
            </tbody>
          </table>
        </div>
      </center>
      <div className="module-details">
        {showDances && (
          <div className="module-card">
            <h3>Dances</h3>
            <table className="module-table">
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Description</th>
                </tr>
              </thead>
              <tbody>
                {dances.map((dance, index) => (
                  <tr key={index}>
                    <td>{dance.name}</td>
                    <td>{dance.description}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
        {showFood && (
          <div className="module-card">
            <h3>Food</h3>
            <table className="module-table">
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Description</th>
                </tr>
              </thead>
              <tbody>
                {food.map((item, index) => (
                  <tr key={index}>
                    <td>{item.name}</td>
                    <td>{item.description}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
        {showArtifacts && (
          <div className="module-card">
            <h3>Artifacts</h3>
            <table className="module-table">
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Description</th>
                </tr>
              </thead>
              <tbody>
                {artifacts.map((artifact, index) => (
                  <tr key={index}>
                    <td>{artifact.name}</td>
                    <td>{artifact.description}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
        {showFestivals && (
          <div className="module-card">
            <h3>Festivals</h3>
            <table className="module-table">
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Description</th>
                </tr>
              </thead>
              <tbody>
                {festivals.map((festival, index) => (
                  <tr key={index}>
                    <td>{festival.name}</td>
                    <td>{festival.description}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
        {showMusic && (
          <div className="module-card">
            <h3>Music</h3>
            <table className="module-table">
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Description</th>
                </tr>
              </thead>
              <tbody>
                {music.map((genre, index) => (
                  <tr key={index}>
                    <td>{genre.name}</td>
                    <td>{genre.description}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default Module;
